    //          //Set values in state which can be extracted in jsx in render. 
             
    //       }})
    //  }
    //  componentWillMount(){
    //      this.readData();
    //  }
    render() {

        return (
            <div>
                <div className="Main_contain">
                    <Header />
                    <div className="form-container">
                        <h1>Track Your Application Status</h1>
                        <p>For security reasons,please answer the fields below to<br /> fetch your applications.</p>
                        <form>
                            <input type="number" className="input-box" placeholder="APPLICATION ID" />
                            <input type="number" className="input-box" placeholder="ENTER YOUR MOBILE NUMBER" />
                            <h2><button type="button" onClick={this.sixdigit} className=" btn button1">TRACK APPLICATION</button></h2>
                            <h5 className="OR">OR</h5>
                            <button type="button" onClick={this.writeData} className="button2">LOGIN</button>
                        </form>
                    </div>

                </div>
            </div>
        )
    }
}
export default Application;
