import React, { Component } from 'react';
import './Application.css';
import Header from '../header/Header';



class Application extends Component{

    state={
        Num:[]
    }
    sixdigit=()=>{
     let unquieNumber= Math.floor(100000 + Math.random() * 900000);
     console.log(unquieNumber);
    let newNum =this.state.Num;
    newNum.push(unquieNumber);
    }

    arrItem=()=>{
        let items = this.state.Num;
     this.setState({selected:items.filter()})
    }
  
render(){
     
    return (
        <div>
        <div className="Main_contain">
        <Header />
             <div className="form-container">
                <h1>Track Your Application Status</h1>
                <p>For security reasons,please answer the fields below to<br /> fetch your applications.</p>
                <form>
                    <input type="email" className="input-box" placeholder="APPLICATION ID"/>
                    <input type="number" className="input-box" placeholder="ENTER YOUR MOBILE NUMBER" />
                    <h2><button type="button" onClick={this.sixdigit} className=" btn button1">TRACK APPLICATION</button></h2>
                     <h5 className ="OR">OR</h5>
                     <button type="button" onClick={this.arrItem} className="button2">LOGIN</button>
                </form>
            </div>
       
        </div>
        </div>
    )
}
}
export default Application;
