import React from 'react';
class Payment extends React.Component {
  doPayment =() => {
    // Publishable Key from Stripe Dashboard
    const stripe =    window.Stripe('pk_test_izTRazS0reCuAYbvSfCVkfAQ004KviIEJL');
    const paymentBtn = document.getElementById('stripe-payment-btn');
    let sessionId;
    paymentBtn.addEventListener('click', () => {
      let orderData = {
      currency: 'EUR',
      quantity: 1,
      amount: 10,
      name: 'Some product',
      description: 'Product description',
      image: 'https://image-url/',
      customerEmail: 'vshrimali709@gmail.com',
      clientId: '12345'
    }
    // Url to Firebase function
    const proxyurl = "https://cors-anywhere.herokuapp.com/";
    const functionURL='https://us-central1-information-management-s-c7f1c.cloudfunctions.net/createOrderAndSession/'
      fetch( proxyurl+functionURL, {
      method: 'POST',
      // Adding the order data to payload
      body: JSON.stringify(orderData)
      }).then(response => {
        return response.json();
      }).then(data => {
        // Getting the session id from firebase function
        var body = JSON.parse(data.body);
        return sessionId = body.sessionId;
      }).then(sessionId => {
        // Redirecting to payment form page
        stripe.redirectToCheckout({
          sessionId: sessionId
        }).then(result=> {
         console.log(result)
        });
      });
    });
  }
// doPayment = async () => {
