//     },
//     body: JSON.stringify({
//       amount: 10,
//       currency: "usd",
//     }),
//   })
//     .then((response) => response.json())
//     .then((responseJson) => {
//       console.log(responseJson);
//     })
//     .catch((error) => {
//       console.error(error);
//     });;
// }
  render() {
    return(
       <div id="stripe-payment-btn">
 <button className="btn" onClick={this.doPayment}> Click here</button>
    </div>
    );
  }
}
export default Payment;
