import React ,{Component} from 'react';
import {Row,Col, Container} from 'react-bootstrap';
import './Footer.css';
import {Link} from "react-router-dom";

class Footer extends Component{

    state={
        year:new Date().getFullYear(),
    }
    render(){
        return(
        
            <div className="footerBottom">
        
               <Container fluid>  
               <Row>
               <Col>
         <p className="CopyRights">Copyright Â© {this.state.year} - All Rights Reserved - Official Website of Rashtriya Mahila Kosh, Ministry of Women and Child Development, Government of India</p>
         </Col>                        
         <Col >      
                            <div className="footerItems">
                                <ul>
                                    <li><Link to="/Home">Home</Link></li>
                                    <li><Link to="/Repayment">Repayment Tracker</Link></li>
                                    <li><Link to="/gallery">Gallery</Link></li>
                                    <li><Link to="/Blog">Blog</Link></li>
                                    <li><Link to="/About">About Us</Link></li>
                                </ul>
                            </div>
                            </Col> 
                            </Row>
                            </Container>
                             
            </div>
         
        );
    }
}
export default Footer;
